import SquareComponent from "./SquareComponent";

function BoardComponent(props) {
  const squareColor = props.style.background;
  const squares = [];
  for (let i = 0; i < 64; i++) {
    squares.push(SquareComponent(i));
  }

  return (
    <div
      style={{
        width: "100%",
        height: "100%",
        display: "flex",
        flexWrap: "wrap",
      }}
    >
      {squares}
    </div>
  );
}
export default BoardComponent;
