import React from "react";
import SquareComponent from "./SquareComponent";

function BoardComponent() {
  const squares = [];
  for (let i = 0; i < 64; i++) {
    squares.push(<SquareComponent />);
    if (i % 2 === 1) {
      this.setState({ bg: "black" });
    }
  }
  return (
    <div
      style={{
        width: "800px",
        height: "800px",
        display: "flex",
        flexWrap: "wrap",
      }}
    >
      {squares}
    </div>
  );
}
export default BoardComponent;
