import React from "react";
// const white = "#fff";
// const black = "#000";
// const color = white;

class SquareComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      bg: "white",
    };
    this.handleClickEvent = this.handleClickEvent.bind(this);
  }

  handleClickEvent() {
    if (this.state.bg === "white") {
      this.setState({ bg: "white" });
    } else {
      this.setState({ bg: "black" });
    }
  }
  //    this.state.bg === "white" ?  this.setState({bg: 'white'}) : "white";

  //   }

  render() {
    return (
      <div
        className="square"
        onClick={this.handleClickEvent}
        style={{ backgroundColor: this.state.bg }}
      ></div>
    );
  }
}

export default SquareComponent;
